pragma solidity ^0.8.19;
import "./Dice.sol";

/*
1. First create dice using the Dice contract
2. Transfer both dice to this contract using the contract's address
3. Use setBattlePair from each player's account to decide enemy
4. Use the battle function to roll, stop rolling and then compare the numbers
5. The player with the higher number gets BOTH dice
6. If there is a tie, return the dice to their previous owner
*/


contract DiceBattle {
    Dice diceContract;
    mapping(address => address) battle_pair;

    enum battleState { tie, win, lose}

    struct battle {
        uint256 battleId;
        battleState state;
        uint256 diceId_1;
        uint256 diceId_2;
        address diceOwner1;
        address diceOwner2;
        address winner;
        address looser;
    }
    uint256 public numBattles = 0;
    mapping(uint256 => battle) public battles;

    event battletie();
    event battlewin();
    event battlelose();
    event add_enemy();

    constructor(Dice diceAddress) public {
        diceContract = diceAddress;
    }

    //function to record a new battle, and add to 'battles' map.
    function addBattle(
        battleState state,
        uint256 diceId_1,
        uint256 diceId_2,
        address diceOwner1,
        address diceOwner2,
        address winner,
        address looser
    ) public returns(uint256) {

        uint256 newBattleId = numBattles++;
        //new battle object
        battle memory newBattle = battle(
            newBattleId,
            state,
            diceId_1,
            diceId_2,
            diceOwner1,
            diceOwner2,
            winner,
            looser
        );

        battles[newBattleId] = newBattle; //commit to state variable
        return newBattleId;   //return new battleId
    }

    function setBattlePair(uint256 myDice, address enemy) public {
        // Require that only prev owner can allow an enemy
        // Each player can only select one enemy
        require(msg.sender == diceContract.getDicePrevOwner(myDice), "only prev onwer can allow an enemy");
        battle_pair[diceContract.getDicePrevOwner(myDice)] = enemy; 

        emit add_enemy();
    }

    function Battle(uint256 myDice, uint256 enemyDice) public {
        // Require that battle_pairs align, ie each player has accepted a battle with the other
        require(battle_pair[diceContract.getDicePrevOwner(myDice)] == diceContract.getDicePrevOwner(enemyDice));
        require(battle_pair[diceContract.getDicePrevOwner(enemyDice)] == diceContract.getDicePrevOwner(myDice));

        diceContract.roll(myDice);
        diceContract.roll(enemyDice);
        diceContract.stopRoll(myDice);
        diceContract.stopRoll(enemyDice);

        uint256 myNum = diceContract.getDiceNumber(myDice);
        uint256 enemyNum = diceContract.getDiceNumber(enemyDice);

        if (myNum == enemyNum)
        {
            diceContract.transfer(myDice, diceContract.getDicePrevOwner(myDice));
            diceContract.transfer(enemyDice, diceContract.getDicePrevOwner(enemyDice));
            addBattle(battleState.tie, myDice, enemyDice, diceContract.getDicePrevOwner(myDice), diceContract.getDicePrevOwner(enemyDice), address(0), address(0));
            emit battletie();
        }
        else if (myNum < enemyNum)
        {
            diceContract.transfer(myDice, diceContract.getDicePrevOwner(enemyDice));
            diceContract.transfer(enemyDice, diceContract.getDicePrevOwner(enemyDice));
            addBattle(battleState.lose, myDice, enemyDice, diceContract.getDicePrevOwner(myDice), diceContract.getDicePrevOwner(enemyDice), diceContract.getDicePrevOwner(enemyDice), diceContract.getDicePrevOwner(myDice));
            emit battlelose();
        }
        else
        {
            diceContract.transfer(myDice, diceContract.getDicePrevOwner(myDice));
            diceContract.transfer(enemyDice, diceContract.getDicePrevOwner(myDice));
            addBattle(battleState.win, myDice, enemyDice, diceContract.getDicePrevOwner(myDice), diceContract.getDicePrevOwner(enemyDice), diceContract.getDicePrevOwner(myDice), diceContract.getDicePrevOwner(enemyDice));
            emit battlewin();
        }
        // Run battle

    }

    //Add relevant getters and setters
    function getBattlePair(address myAddress) public view returns(address)
    {
        return battle_pair[myAddress];
    }

    function getWinner(uint256 battleId) public view returns (address) 
    {
        return battles[battleId].winner;
    }

    function getState(uint256 battleId) public view returns (battleState) 
    {
        return battles[battleId].state;
    }

    function getDiceId1(uint256 battleId) public view returns (uint256) 
    {
        return battles[battleId].diceId_1;
    }

    function getDiceId2(uint256 battleId) public view returns (uint256) 
    {
        return battles[battleId].diceId_2;
    }

    function getAddress1(uint256 battleId) public view returns (address) 
    {
        return battles[battleId].diceOwner1;
    }

    function getAddress2(uint256 battleId) public view returns (address) 
    {
        return battles[battleId].diceOwner2;
    }
}
